const axios = require('axios');

const getNearbyByCategoryId = async (clientId, clientSecret, categoryId) => {
    return axios.get('https://api.foursquare.com/v2/venues/explore', {
        params: {
            client_id: clientId,
            client_secret: clientSecret,
            categoryId,
            near: 'Toronto',
            radius: 2000,
            v: 20190425,
        }
    });
}

module.exports = {
    getNearbyByCategoryId,
}
