const { createLogger, format, transports } = require('winston');

module.exports = createLogger({
    levels: {
        error: 0,
        warn: 1,
        info: 2,
    },
    level: 'info',
    exitOnError: false,
    format: format.combine(
        format.timestamp(),
        format.splat(),
        format.json(),
        format.errors({ stack: true }),
    ),
    transports: [
        new transports.Console({ silent: process.env.Env === 'test' }),
    ],
});
